    private void b_alterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_alterarActionPerformed

        try {
            // recebendo e validando dados da interface gr�fica.
            int id = 0;
            String nome = "";
            String marca = "";
            double valor = 0;
            String setor = "";
            int estoque = 0;

            if (this.c_nome.getText().length() < 2) {
                throw new Mensagens("Nome deve conter ao menos 2 caracteres.");
            } else {
                nome = this.c_nome.getText();
            }

            if (this.c_marca.getText().length() < 2) {
                throw new Mensagens("Marca deve conter ao menos 2 caracteres.");
            } else {
                marca = this.c_marca.getText();
            }

            if (this.c_valor.getText().length() <= 0) {
                throw new Mensagens("Valor deve ser número e maior que zero.");
            } else {
                valor = Double.parseDouble(this.c_valor.getText());
            }

            if (this.c_setor.getText().length() < 2) {
                throw new Mensagens("Setor deve conter ao menos 2 caracteres.");
            } else {
                setor = this.c_setor.getText();
            }
             if (this.c_estoque.getText().length() <= 0) {
                throw new Mensagens("Estoque deve ser número e maior que zero.");
            } else {
                estoque = Integer.parseInt(this.c_estoque.getText());
            }

            if (this.jTableFerramenta.getSelectedRow() == -1) {
                throw new Mensagens("Primeiro Selecione uma Ferramenta para Alterar");
            } else {
                id = Integer.parseInt(this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 0).toString());
            }

            // envia os dados para o Aluno processar
            if (this.objferramenta.UpdateFerramentaBD(id, nome, marca, valor, setor, estoque )) {

                // limpa os campos
                this.c_nome.setText("");
                this.c_marca.setText("");
                this.c_valor.setText("");
                this.c_setor.setText("");
                this.c_estoque.setText("");
                JOptionPane.showMessageDialog(rootPane, "Ferramenta Alterada com Sucesso!");

            }
            System.out.println(this.objferramenta.getListaFerramenta().toString());
        } catch (Mensagens erro) {
            JOptionPane.showMessageDialog(null, erro.getMessage());
        } catch (NumberFormatException erro2) {
            JOptionPane.showMessageDialog(null, "Informe um número.");
        } finally {
            carregaTabela(); // atualiza a tabela.
        }
    }//GEN-LAST:event_b_alterarActionPerformed

    private void jTableFerramentaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTableFerramentaMouseClicked

        if (this.jTableFerramenta.getSelectedRow() != -1) {

            String nome = this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 1).toString();
            String marca  = this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 2).toString();
            String valor = this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 3).toString();
            String setor = this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 4).toString();
            String estoque = this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 5).toString();

            this.c_nome.setText(nome);
            this.c_marca.setText(marca);
            this.c_valor.setText(valor);
            this.c_setor.setText(setor);
            this.c_estoque.setText(estoque);

        }
    }//GEN-LAST:event_jTableFerramentaMouseClicked

    private void b_apagarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_apagarActionPerformed
        try {
            // validando dados da interface gr�fica.
            int id = 0;
            if (this.jTableFerramenta.getSelectedRow() == -1) {
                throw new Mensagens("Primeiro Selecione uma Ferramenta para APAGAR");
            } else {
                id = Integer.parseInt(this.jTableFerramenta.getValueAt(this.jTableFerramenta.getSelectedRow(), 0).toString());
            }

            // retorna 0 -> primeiro bot�o | 1 -> segundo bot�o | 2 -> terceiro bot�o
            int resposta_usuario = JOptionPane.showConfirmDialog(null, "Tem certeza que deseja APAGAR esta Ferramenta ?");

            if (resposta_usuario == 0) {// clicou em SIM

                // envia os dados para o Aluno processar
                if (this.objferramenta.DeleteFerramentaBD(id)) {

                    // limpa os campos
                    this.c_nome.setText("");
                    this.c_marca.setText("");
                    this.c_valor.setText("");
                    this.c_setor.setText("");
                    this.c_estoque.setText(""); 
                    JOptionPane.showMessageDialog(rootPane, "Ferramenta Apagada com Sucesso!");

                }

            }

            System.out.println(this.objferramenta.getListaFerramenta().toString());

        } catch (Mensagens erro) {
            JOptionPane.showMessageDialog(null, erro.getMessage());
        } finally {
            // atualiza a tabela.
            carregaTabela();
        }
    }//GEN-LAST:event_b_apagarActionPerformed

