    /**
     * @param args the command line arguments
     */
    @SuppressWarnings("unchecked")
    public void carregaTabela() {

        DefaultTableModel modelo = (DefaultTableModel) this.jTableFerramenta.getModel();
        modelo.setNumRows(0);

        ArrayList<Ferramenta> listaferramenta = new ArrayList<>();
        listaferramenta = objferramenta.getListaFerramenta();

        for (Ferramenta a : listaferramenta) {
            modelo.addRow(new Object[]{
                a.getIdf(),
                a.getNome(),
                a.getMarca(),
                a.getValor(),
                a.getSetor(),
                a.getEstoque()  
            });
        }
    }

